mapper.delete:
delete
from tb_collect c
where c.userId = #{uerId}
  and c.productId = #{productId}

mapper.select:
select *
from tb_collect c
where c.collectId = #{collectId}

select u.*, c.*, p.*
from tb_user u left join tb_collect c on (u.userId = c.userId)
            left join tb_product p on (u.productId = p.productId)
where c.userId = #{userId}

select * from tb_collect c


        and c.userId = #{userId}


        and c.productId = #{productId}


        and c.collectTime = #{collectTime}
